import axios from "axios";
import { useLocation } from "react-router";
import { useContext, useEffect, useState  } from "react";
import { Context } from "../../context/Context";
import "./singlepost.css";

export default function SinglePost() {
  const publicFolder = "http://localhost:5000/images/";
  const location = useLocation()
  const path = location.pathname.split('/')[2];
  const [post, setPost] = useState({});
  const { user } = useContext(Context);

  useEffect(() => {
    const getPost = async () => {
    const res = await axios.get("/posts/" + path);
    setPost(res.data);
    // setTitle(res.data.title);
    // setDesc(res.data.desc)
    };

    getPost();
  }, [path]);

  const handleDelete = async (e) => {

    try {

      await axios.delete("/posts/" + path, {username: user.username});
      window.location.replace("/");

    } catch (error) {}
  }

  return (
    <div className="single-post">
      <div className="single-post-wrap">
      {post.photo && (
        <img
          src={publicFolder + post.photo}
          alt=""
          className="single-post-img"
        />
      )}

        <h1 className="single-post-title">
         {post.title}
          {post.username === user?.username && (
             <div className="post-edit-container">
            <i 
              className="edit-icon far fa-edit">

            </i>
            <i 
              className="edit-icon fa-solid fa-trash-can"
              onClick={handleDelete}  
            >

            </i>
          </div>
          )}
        </h1>
        <div className="post-info">
          <span className="author">{post.username}</span>
          <span>{new Date(post.createdAt).toDateString()}</span>
        </div>
        <p className="single-post-description">{post.desc}</p>
      </div>
    </div>
  );
}
