import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
import { Context } from "../../context/Context";
import './write.css'

export default function Write() {
  const location = useLocation()
  const path = location.pathname.split('/')[2];
  const [post, setPost] = useState({});
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const { user } = useContext(Context);

  useEffect(() => {
    const getPost = async () => {
    const res = await axios.get("/posts/" + path);
    setPost(res.data);
    setTitle(res.data.title);
    setDesc(res.data.desc)
    };

    getPost();
  }, [path]);


  const handleSubmit = async(e) => {
    e.preventDefault();
    const newPost = {
      username: user.username,
      title,
      desc
    }
    if(file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      newPost.photo = filename;
      try {
        await axios.post("/upload", data)
      } catch (error) {

      }
      try {
        const res = await axios.post("/posts", newPost);
        window.location.replace("/post/" + res.data._id);
      } catch (error) {

      }

    }
    axios.post("/posts");
  };

  return (
    <div className="write">
    {file && <img className="write-img"
        src="https://images.pexels.com/photos/1105666/pexels-photo-1105666.jpeg?cs=srgb&dl=pexels-vishnu-r-nair-1105666.jpg&fm=jpg"
        alt=""
      />}

      <form className="write-form" onSubmit={handleSubmit}>
        <div className="write-form-row">
          <label htmlFor="file-input">
            <i className="write-icon fas fa-plus"></i>
          </label>
          <input 
            type="file" 
            id="file-input" 
            style={{ display: "none" }} 
            onChange={(e) => setFile(e.target.files[0])}  
            />
          <input
            className="write-input"
            type="text"
            placeholder="Title"
            autoFocus={true}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className="write-form-row">
          <textarea
            className="write-input write-text"
            placeholder="Share your insight..."
            type="text"
            autoFocus={true}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <button className="write-submit" type="submit">Publish</button>
      </form>
    </div>
  );
}
