import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { useLocation } from "react-router";
import { Link } from "react-touter-dom";
import { Context } from "../../context/Context";
import "./singlepost.css";


export default function SinglePost() {
  const location = useLocation()
  const path = location.pathname.split('/')[2];
  const [post, setPost] = useState({});
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const { user } = useContext(Context);

  useEffect(() => {
    const getPost = async () => {
    const res = await axios.get("/posts/" + path);
    setPost(res.data);
    setTitle(res.data.title);
    setDesc(res.data.desc)
    };

    getPost();
  }, [path]);

  return (
    <div className="single-post">
      <div className="single-post-wrap">
      {post.photo && (
        <img
          src={post.photo}
          alt=""
          className="single-post-img"
        />
      )}

        <h1 className="single-post-title">
         {post.title}
          <div className="post-edit-container">
            <i className="edit-icon far fa-edit"></i>
            <i className="edit-icon fa-solid fa-trash-can"></i>
          </div>
        </h1>
        <div className="post-info">
          <span className="author">{post.username}</span>
          <span>{new Date(post.createdAt).toDateString()}</span>
        </div>
        <p className="single-post-description">{post.desc}</p>
      </div>
    </div>
  );
}
